import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const MedicineView = () => {
    const [medicines, setMedicines] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const navigate = useNavigate();

    const fetchMedicines = (e) => {
        e.preventDefault();

        axios.get(`https://drug-info-and-price-history.p.rapidapi.com/1/druginfo?drug=${searchTerm}`, {
            "headers": {
                'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
                'X-RapidAPI-Host': 'drug-info-and-price-history.p.rapidapi.com'
            }
        })
            .then(res => {
                // setMedicines(res.data[0]);
                console.log(res.data[0])
                console.log(medicines.brand_name)
                // navigate(`/medInfo/${medicines.brand_name}`);
            })
            .catch(err => {
                console.log(err);
            });
    };


    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
    };

    return (
        <div>
            <h1>Medicines</h1>
            <form onSubmit={fetchMedicines}>
                <input type="text" value={searchTerm} onChange={handleSearch} />
                <input type="submit" />
            </form>
        </div>
    );
};

export default MedicineView;
