import React, { useState } from 'react';
import axios from 'axios'

const LifeExpectancyView = () => {

  const [birthDate, setBirthDate] = useState('');
  const [gender, setGender] = useState('male');
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const options = {
      method: 'GET',
      url: 'https://life-left.p.rapidapi.com/time-left',
      params: {
        birth: birthDate,
        gender: gender,
      },
      headers: {
        'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
        'X-RapidAPI-Host': 'life-left.p.rapidapi.com',
      },
    };

    try {
      const response = await axios.request(options);
      setResult(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Life Left Calculator</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="birthDate">Birth Date:</label>
          <input
            type="text"
            id="birthDate"
            value={birthDate}
            onChange={(e) => setBirthDate(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="gender">Gender:</label>
          <select
            id="gender"
            value={gender}
            onChange={(e) => setGender(e.target.value)}
          >
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </div>
        <button type="submit">Calculate</button>
      </form>
      {result && (
        <div>
          <h2>Result:</h2>
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default LifeExpectancyView
