import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DashboardView = () => {
    const [medicine, setMedicine] = useState(null); // Change initial state to null

    useEffect(() => {
        axios.get("https://drug-info-and-price-history.p.rapidapi.com/1/druginfo?drug=advil", {
            "headers": {
                'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
                'X-RapidAPI-Host': 'drug-info-and-price-history.p.rapidapi.com'
            }
        })
            .then(res => {
                setMedicine(res.data[0]); // Update to res.data[0] if the API returns an array
                console.log(res.data[0]);
            })
            .catch(err => {
                console.log(err);
            });
    }, []);

    if (!medicine) {
        return <div>Loading...</div>; // Add loading message or component while waiting for data
    }

    return (
        <div>
            <h1>BrandName: {medicine.generic_name} {medicine.application_number}</h1>
        </div>
    );
}

export default DashboardView;
