import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DashboardView = () => {
    const [drugInfo, setDrugInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDrugInfo = async () => {
            const options = {
                method: 'GET',
                url: 'https://drug-info-and-price-history.p.rapidapi.com/1/druginfo',
                params: { drug: 'advil' },
                headers: {
                    'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
                    'X-RapidAPI-Host': 'drug-info-and-price-history.p.rapidapi.com'
                }
            };

            try {
                const response = await axios.request(options);
                setDrugInfo(response.data);
                setLoading(false);
            } catch (error) {
                setError('Failed to fetch drug information.');
                setLoading(false);
                console.error(error);
            }
        };

        fetchDrugInfo();
    }, []);

    return (
        <div>
            <h1>My Drug App</h1>
            {loading ? (
                <p>Loading...</p>
            ) : error ? (
                <p>{error}</p>
            ) : drugInfo ? (
                <div>
                    <h2>{drugInfo.generic_name}</h2>
                    <p>Price: {drugInfo.price}</p>
                    <p>Manufacturer: {drugInfo.manufacturer}</p>
                    {/* Display other relevant information */}
                </div>
            ) : (
                <p>No drug information available.</p>
            )}
        </div>
    );
};

export default DashboardView;
