// MedInfo.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const MedInfo = () => {
    const { id } = useParams();
    const [medicines, setMedicines] = useState({});

    useEffect(() => {
        axios.get(`https://drug-info-and-price-history.p.rapidapi.com/1/druginfo?drug=${id}`, {
            "headers": {
                'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
                'X-RapidAPI-Host': 'drug-info-and-price-history.p.rapidapi.com'
            }
        })
            .then(res => {
                setMedicines(res.data[0]);
                console.log(res.data[0]);
            })
            .catch(err => {
                console.log(err);
                setMedicines({});
            });
    }, [id]);

    return (
        <div>
            <h1>Medicine Info</h1>
            <p>Generic Name: {medicines.generic_name}</p>
            <p>Brand Name: {medicines.brand_name}</p>
            <p>Route: {medicines.route} </p>
            <p>Dosage form: {medicines.dosage_form}  </p>
            <p>Ingredients: {medicines.active_ingredients.name} </p>
        </div>
    );
};

export default MedInfo;
