import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const MedicineView = () => {
    const [searchMed, setSearchMed] = useState('');
    const [searchSymptom, setSearchSymptom] = useState('')
    const [symptom, setSymptom] = useState('')
    const navigate = useNavigate();

    const fetchMedicines = (e) => {
        e.preventDefault();

        axios.get(`https://drug-info-and-price-history.p.rapidapi.com/1/druginfo?drug=${searchMed}`, {
            "headers": {
                'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
                'X-RapidAPI-Host': 'drug-info-and-price-history.p.rapidapi.com'
            }
        })
            .then(res => {
                console.log(res.data[0])
                navigate(`/medInfo/${res.data[0].brand_name}`);
            })
            .catch(err => {
                console.log(err);
            });
    };



    const fetchSymptoms = (e) => {
        e.preventDefault();
        axios.post('https://symptom-checker4.p.rapidapi.com/analyze', {
            symptoms: searchSymptom
        }, {
            headers: {
                'content-type': 'application/json',
                'X-RapidAPI-Key': '8d95881717mshb150bac71409acbp1dbee3jsn2097310c3b61',
                'X-RapidAPI-Host': 'symptom-checker4.p.rapidapi.com'
            }
        })
            .then(res => {
                console.log(res.data);
                // navigate('/symptom')
            })
            .catch(err => {
                console.log(err);
            })
    }


    const handleSearch = (e) => {
        setSearchMed(e.target.value);
    };

    const handleSymptom = (e) => {
        setSymptom(e.target.value)
    }

    const handleSearchSymptom = (e) => {
        setSearchSymptom(e.target.value)
    }

    return (
        <div>
            <div>
                <h1>Medicines</h1>
                <form onSubmit={fetchMedicines}>
                    <input type="text" value={searchMed} onChange={handleSearch} />
                    <input type="submit" />
                </form>
            </div>

            <div>
                <h1>Enter your symptom</h1>
                <form onSubmit={fetchSymptoms} >
                    <div>
                        <label>Symptom</label>
                        <input type="text" value={symptom} onChange={handleSymptom} />
                    </div>
                    <div>
                        <label>Describe the symptom</label>
                        <textarea type="text" rows='4' cols='50' value={searchSymptom} onChange={handleSearchSymptom} />
                    </div>
                    <input type="submit" />
                </form>
            </div>

        </div>
    );
};

export default MedicineView;
