import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MedicineView = () => {
    const [medicines, setMedicines] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    const fetchMedicines = () => {
        axios.get(`https://drug-info-and-price-history.p.rapidapi.com/1/druginfo?drug=${searchTerm}`, {
            "headers": {
                'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
                'X-RapidAPI-Host': 'drug-info-and-price-history.p.rapidapi.com'
            }
        })
            .then(res => {
                setMedicines(res.data);
                console.log(res.data);
            })
            .catch(err => {
                console.log(err);
            });
    };

    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
    };

    useEffect(() => {
        fetchMedicines();
    }, [searchTerm]);

    return (
        <div>
            <h1>Medicines</h1>
            <form>
                <input type="text" value={searchTerm} onChange={handleSearch} placeholder="Enter medicine name" />
                {/* Add other form elements if needed */}
            </form>
            {medicines.length > 0 ? (
                <div>
                    {medicines.map((medicine, index) => (
                        <h1 key={index}>{medicine.generic_name}</h1>
                    ))}
                </div>
            ) : (
                <p>No medicines found.</p>
            )}
        </div>
    );
};

export default MedicineView;
