import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const MedicineView = () => {
    const [medicines, setMedicines] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [symptom, setSymptom] = useState('')
    const navigate = useNavigate();

    const fetchMedicines = (e) => {
        e.preventDefault();

        axios.get(`https://drug-info-and-price-history.p.rapidapi.com/1/druginfo?drug=${searchTerm}`, {
            "headers": {
                'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
                'X-RapidAPI-Host': 'drug-info-and-price-history.p.rapidapi.com'
            }
        })
            .then(res => {
                setMedicines(res.data[0]);
                console.log(res.data[0])
                console.log(medicines.brand_name)
                navigate(`/medInfo/${res.data[0].brand_name}`);
            })
            .catch(err => {
                console.log(err);
            });
    };



    const fetchSymptoms = (e) => {
        e.preventDefault();
        axios
            .post(
                `https://symptom-checker4.p.rapidapi.com/analyze`,
                {
                    symptoms: [symptom], // Correct the format to an array of symptoms
                },
                {
                    headers: {
                        'content-type': 'application/json',
                        'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
                        'X-RapidAPI-Host': 'symptom-checker4.p.rapidapi.com',
                    },
                }
            )
            .then((res) => {
                console.log(res.data);
            })
            .catch((err) => {
                console.log(err);
            });
    };



    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleSymptom = (e) => {
        setSymptom(e.target.value)
    }

    return (
        <div>
            <div>
                <h1>Medicines</h1>
                <form onSubmit={fetchMedicines}>
                    <input type="text" value={searchTerm} onChange={handleSearch} />
                    <input type="submit" />
                </form>
            </div>

            <div>
                <h1>Enter your symptom</h1>
                <form onSubmit={fetchSymptoms} >
                    <div>
                        <label>Symptom</label>
                        <input type="text" value={searchTerm} onChange={handleSearch} />
                    </div>
                    <div>
                        <label>Describe the symptom</label>
                        <textarea type="text" rows='4' cols='50' value={symptom} onChange={handleSymptom} />
                    </div>
                    <input type="submit" />
                </form>
            </div>

        </div>
    );
};

export default MedicineView;
