import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const MedicineView = () => {
    const [searchMed, setSearchMed] = useState('');
    const [medicines, setMedicines] = useState({});

    const fetchMedicines = (e) => {
        e.preventDefault();
        axios.get(`https://drug-info-and-price-history.p.rapidapi.com/1/druginfo?drug=${searchMed}`, {
            "headers": {
                'X-RapidAPI-Key': 'c8a8e86c4bmshaf6ec9cd4fafff7p1bc63ejsn8b52e0af2dde',
                'X-RapidAPI-Host': 'drug-info-and-price-history.p.rapidapi.com'
            }
        })
            .then(res => {
                console.log(res.data[0])
                setMedicines(res.data[0])
            })
            .catch(err => {
                console.log(err);
            });
    };


    const handleSearch = (e) => {
        setSearchMed(e.target.value);
    };



    return (
        <div>
            <div>
                <button className='medButton' ><Link to='/' >Home</Link></button>
            </div>
            <div>
                <h1>Medicines</h1>
                <form onSubmit={fetchMedicines}>
                    <input type="text" value={searchMed} onChange={handleSearch} />
                    <input type="submit" />
                </form>
            </div>
            <div>
                <h2>Medication Result</h2>
                <p>Brand Name: {medicines.brand_name}</p>
                <p>Route: {medicines.route} </p>
                <p>Dosage form: {medicines.dosage_form}  </p>
            </div>

        </div>
    );
};

export default MedicineView;
